import Layout from "../components/layout/Layout";
import React, { useEffect, useState } from 'react'
import axios from "axios"
import { useParams } from "react-router-dom";

const ProductDetails = () => {

    const params = useParams();
    const [product, setProduct] = useState({})
    const [relatedP, setRelatedP] = useState([])

    const getProduct = async() =>{
        try {
            const {data} = await axios.get(`${process.env.REACT_APP_API}/api/v1/product/get-single-product/${params?.slug}`)
            setProduct(data?.products)
            getSimilarProducts(data?.products._id, data?.products.category._id)
        } catch (error) {
            console.log(error)
        }
    }

    useEffect(()=>{
        getProduct();
    },[params?.slug])

    const getSimilarProducts = async(cid, pid) =>{
        try {
            const {data} = axios.get(`${process.env.REACT_APP_API}/api/v1/product/related-product/${pid}/${cid}`)
            setRelatedP(data?.products)
        } catch (error) {
            console.log(error)
        }
    }
  return (
    <Layout>
        <h1>Product Details</h1>
        <div className="row container mt-2">
            <div className="col-md-6">
            <img 
                src={`${process.env.REACT_APP_API}/api/v1/product/get-product-photo/${product._id}`} className="card-img-top" 
                alt={product.name} 
                style={{height: '15rem'}}/>
            </div>
            <div className="col-md-6">
                <h1 className="text-center"> PRODUCT DETAILS</h1>
                <h6>Name: {product.name}</h6>
                <h6>Description: {product.description}</h6>
                <h6>Price: {product.price}</h6>
                <h6>Category: {product?.category.name}</h6>
                {/* <h6> Shipping: {product.shipping}</h6> */}
                <div className="card-name-price">                    
                    <button className="btn btn-secondary ms-1">ADD TO CART</button>
                </div>
            </div>
            <div className="row">
                <h1>Similar Products</h1>
                {JSON.stringify(relatedP, null, 4)}
            </div>

        </div>

    </Layout>
  )
}

export default ProductDetails
