import React from "react";
import Footer from "./Footers";
import Header from "./headers";
import {Helmet} from "react-helmet";

const Layout = ({children}) => {
    return(
        <>
            <Helmet>
                <meta charSet="utf-8" />
                <title>My Title</title>
            </Helmet>
           <Header />
           <main style={{minHeight: "75vh"}}>
                {children}
           </main>
           <Footer />
        </>
    )
}

export default Layout
