import Layout from "../components/layout/Layout";
import React, { useEffect, useState } from 'react'
import axios from "axios"
import { useParams } from "react-router-dom";

const ProductDetails = () => {

    const params = useParams();
    const [product, setProduct] = useState({})
    const getProduct = async() =>{
        try {
            const {data} = await axios.get(`${process.env.REACT_APP_API}/api/v1/product/get-single-product/${params?.slug}`)
            setProduct(data?.products)
        } catch (error) {
            console.log(error)
        }
    }

    useEffect(()=>{
        getProduct();
    },[params?.slug])
  return (
    <Layout>
        <h1>Product Details</h1>
       <div className="col-md-5">
       <img src={`${process.env.REACT_APP_API}/api/v1/product/get-product-photo/${product._id}`} className="card-img-top" alt={product.name} style={{height: '15rem'}}/>
       </div>
       <div className="col-md-5">{product.name}</div>
       <div className="col-md-5"></div>
    </Layout>
  )
}

export default ProductDetails
