import React,{useState, useEffect
} from 'react'
import Layout from '../../components/layout/Layout'
import AdminMenu from '../../components/layout/AdminMenu'
import toast from 'react-hot-toast'
import axios from 'axios'
import {Select} from 'antd'
import { useNavigate, useParams } from 'react-router-dom'

const {Option} = Select
const UpdateProduct = () => {

    const params = useParams();
    const [categories, setCategories] = useState([]);
    const [name, setName] = useState("");
    const [description, setDescription] = useState("");
    const [price, setPrice] = useState("");
    const [category, setCategory] = useState("");
    const [quantity, setQuantity] = useState("");
    const [shipping, setShipping] = useState("");
    const [photo, setPhoto] = useState("");
    const [id, setId] = useState("");

    const navigate = useNavigate();

    const getSingleProduct = async()=>{
        try {
            const {data} = await axios.get(`${process.env.REACT_APP_API}/api/v1/product/get-single-product/${params.slug}`)
            setName(data.products.name)
            setDescription(data.products.description)
            setId(data.product._id);
            setPrice(data.products.price)
            setCategory(data.products.category)
            setQuantity(data.products.quantity)
            setShipping(data.products.shipping)
            setPhoto(data.products.photo)

        } catch (error) {
            console.log(error)
            toast.error("Something went wrong while getting single product")
        }
    }

    useEffect(()=>{
        getSingleProduct();
        //eslint-disable-next-line
      },[])

    const handleUpdate = async (event) => {
        event.preventDefault()
        try {
            const productData = new FormData();
            productData.append("name", name);
            productData.append("description", description);
            productData.append("price", price);
            productData.append("quantity", quantity);
            productData.append("photo", photo);
            productData.append("category", category);
            productData.append("shipping", shipping);
            const { data } = await axios.post(
            `${process.env.REACT_APP_API}/api/v1/product/create-product`,
            productData
            )
        // const productData = new FormData(event.target)
        // const {data} = await axios.post(`${process.env.REACT_APP_API}/api/v1/category/create-category`, productData, {
        //   headers: {
        //     'Content-Type': 'multipart/form-data' // Important for file uploads
        //   }
        // })

        if (data?.success) {
            toast.success("Product Created Successfully");
            navigate("/dashboard/admin/products");
        } else {
            toast.error(data?.message);
        }
        } catch (error) {
        console.log(error)
        }
    }


  return (
    <Layout title={"Dashboard- Update Product"}>
        <div className='container-fluid m-3 p-3'>
          <div className="row">
              <div className="col-md-3">
                  <AdminMenu/>
              </div>    
              <div className="col-md-9">
                  <h1>Update Product</h1>
                  <form onSubmit={handleUpdate}>
                    <div className='m-1 w-75' >
                      <Select 
                        placeholder="Select a Category" 
                        size='large' 
                        showSearch 
                        className='form-select mb-3' 
                        onChange={(value) => setCategory(value)}>

                      {
                        categories?.map(item => {
                          return(
                          <Option style={{ border: 'none' }} key={item._id} value={item._id}>{item.name}</Option>)
                        })
                      }
                      </Select>
                      <div className="mb-3">
                        <label className="btn btn-outline-secondary col-md-12">
                          {photo ? photo.name : "Upload Photo"}
                          <input
                            type="file"
                            name="photo"
                            accept="image/*"
                            onChange={(e) => setPhoto(e.target.files[0])}
                            hidden
                          />
                        </label>
                      </div>
                      <div className="mb-3">
                        {photo && (
                          <div className="text-center">
                            <img
                              src={URL.createObjectURL(photo)}
                              alt="product_photo"
                              height={"200px"}
                              className="img img-responsive"
                            />
                          </div>
                        )}
                      </div>
                      <div className="mb-3">
                        <input
                        type="text"
                        value={name}
                        placeholder="write a name"
                        className="form-control"
                        onChange={(e) => setName(e.target.value)}
                      />
                      </div>
                      <div className="mb-3">
                        <textarea
                          type="text"
                          value={description}
                          placeholder="write a description"
                          className="form-control"
                          onChange={(e) => setDescription(e.target.value)}
                        />
                      </div>
                      <div className="mb-3">
                        <input
                          type="number"
                          value={price}
                          placeholder="write a Price"
                          className="form-control"
                          onChange={(e) => setPrice(e.target.value)}
                        />
                      </div>
                      <div className="mb-3">
                        <input
                          type="number"
                          value={quantity}
                          placeholder="write a quantity"
                          className="form-control"
                          onChange={(e) => setQuantity(e.target.value)}
                        />
                      </div>
                      <div className="mb-3">
                        <Select
                          placeholder="Select Shipping "
                          size="large"
                          showSearch
                          className="form-select mb-3"
                          onChange={(value) => {
                            setShipping(value);
                          }}
                          value={shipping ? "yes" : "No"}
                        ><Option value="0">No</Option>
                        <Option value="1">Yes</Option>
                      </Select>
                      </div>
                      <div className="mb-3">
                        <button className="btn btn-primary" type="submit">UPDATE PRODUCT</button>
                      </div>
                    </div> 
                  </form>   
              </div> 
          </div>
        </div> 
    </Layout>
  )
}

export default UpdateProduct
