import React, { useState, useEffect } from "react";
import Layout from "../components/layout/Layout"
import { useAuth } from "../Context/auth"
import { Link, useNavigate } from "react-router-dom";
import toast from "react-hot-toast";
import axios from "axios";
import { Checkbox } from "antd";

const HomePage = () => {
    const [auth, setAuth] = useAuth()
    const navigate = useNavigate();
    const [products, setProducts] = useState([]);
    const [categories, setCategories] = useState([]);
    const [checked, setChecked] = useState([]);

    const getAllProducts = async()=>{
        try {
            const {data} = await axios.get(`${process.env.REACT_APP_API}/api/v1/product/get-product`)
            if(data.successful){
                console.log(data.products)
                setProducts(data.products)
            }
        } catch (error) {
            console.log(error)
            toast.error("Something went wrong in getting products")
        }
    }
    const getAllCategory = async () => {
        try {
            const {data} = await axios.get(`${process.env.REACT_APP_API}/api/v1/category/get-category`)
            if(data?.success){
              setCategories(data?.category)
            }
        } catch (error) {
            console.log(error)
            toast.error("something went wrong in getting category")
        }
    }
    const handleFilter = (value, id) => {
        try{
            let all = [...checked]
            if(value){
            all.push(id)
            }else{
            all = all.filter(item => item !== id)
            }
        }catch(error) {
            console.log(error)
        }
        setChecked(all)
    }
    useEffect (()=>{
        getAllProducts();
        getAllCategory();

    },[])
    return(
        <Layout title="All Products- Best Offers">
            <div className="row mt-3">
                <div className="col-md-2">
                    <h4>Filter by Category</h4>
                    {
                        categories?.map(item => {
                            return(
                                <Checkbox key={item._id} onChange={ (e) => handleFilter(e.target.checked, item._id)}>
                                    {item.name}
                                </Checkbox>)

                        })}
                </div>
                <div className="col-md-9">
                {JSON.stringify(checked,null,4)}
                    <h1 className="text-center">All Products</h1>
                    <div className="d-flex flex-wrap">
                        {
                        products?.map(item => {
                            return(
                                <Link to={""} key={item._id} className="product-link">
                                    <div className="card m-3" style={{width: '18rem'}} >

                                    <img src={`${process.env.REACT_APP_API}/api/v1/product/get-product-photo/${item._id}`} className="card-img-top" alt={item.name} />
                                    <div className="card-body">
                                        <h5 className="card-title">{item.name}</h5>
                                        <p className="card-text">{item.price}</p>
                                        <p className="card-text">{item.description}</p>
                                        <p className="btn btn-primary">Go somewhere</p>
                                    </div>
                                    </div>
                                </Link>                           
                            )
                        })
                    }
                    </div>

                </div>
            </div>
        </Layout>
    )
}

export default HomePage
